huma = Table('tiger.txt', rime=1)

for chars, filename in inputs:
    b = []
    nob = []
    for ch in chars:
        code = huma.t2c[ch][0]
        if 'b' in code:
            b.append(ch)
        else:
            nob.append(ch)
    with open(filename + '含b.txt', 'w') as f:
        print(''.join(b), file=f)
    with open(filename + '不含b.txt', 'w') as f:
        print(''.join(nob), file=f)
